class TBTask:
    def __init__(self, args):
        self.__dict__.update(vars(args))
        self.free_answer = False
        self.max_turn = 20
        self.prompts = import_prompts_by_task("tb")
        self.set = []
        self.data = self.load_dataset(args.dataset)
        self.root = None

    def load_dataset(self, name):
        if name == "FloDial":
            self.set = list(trouble_name.values())
            return load_flodial_dataset(os.path.join(os.path.dirname(__file__), "../data/FloDial_stat.xlsx"))
        else:
            raise NotImplementedError

    def create_root(self, root=None):
        if not root:
            self.root = UoTNode("ROOT", True, self.set, None, self.guesser_model)
        else:
            root.n_extend_layers = self.n_extend_layers
            self.root = root


def load_flodial_dataset(file_path):
    repo_dataset = []
    df = pd.read_excel(file_path)
    for _, row in df.iterrows():
        repo_dataset.append({'self_repo': row['Self-repo'], 'target': row['Item']})
    return repo_dataset

